<style lang="scss" scoped>
.socials {
  @include reset-list;
  display: flex;
  flex-wrap: wrap;
  row-gap: 15px;

  @media (min-width: $tablet-width) {
    width: 186px;
  }
}


.socials__item a {
  display: block;
  width: 45px;
  height: 45px;

  @media (min-width: $tablet-width) {
    width: 62px;
    height: 62px;
  }
}

.socials__item a {
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  
  &:active svg {
    opacity: 0.3;
  }
}

.socials--footer .socials__item a {
  background-color: $basic-yellow;

  &:hover,
  &:focus {
    background-color: $special-yellow-dark;
  }
}

.socials__item--fb svg {
  @media (min-width: $tablet-width) {
    width: 14px;
    height: 26px;
  }
}

.socials__item--vk svg {
  @media (min-width: $tablet-width) {
    width: 28px;
    height: 18px;
  }
}

.socials__item--inst svg {
  @media (min-width: $tablet-width) {
    width: 22px;
    height: 22px;
  }
}
</style>